import React from "react";
import { getPokemons } from "../../core/repositories/pokemons.repository";
import PokemonCard from "../../components/pokemon-card";
import useAsyncHook from "../../core/hooks/hooks";

import { useAsync } from "react-async";

const Home = () => {
  const [pokemons, error, loading] = useAsyncHook();

  console.log("pokemons", pokemons);
  console.log("error", error);
  console.log("loading", loading);

  if (pokemons.length > 0) {
    const listPokemon = pokemons.map((pokemon) => (
      <li>
        <PokemonCard />
      </li>
    ));
  }

  return (
    <div>
      <nav class="navbar navbar-light bg-light">
        <div class="container-fluid">
          <form class="d-flex">
            <input
              class="form-control me-2"
              type="search"
              placeholder="Buscar Pokemon"
              aria-label="Search"
            ></input>
            <button class="btn btn-outline-success" type="submit">
              Buscar
            </button>
          </form>
        </div>
      </nav>

      <div class="container">
        <div class="row">
          <div class="col-sm-2">
            {" "}
            {pokemons.length == 0 ? (
              <h1>Esta cargando</h1>
            ) : (
              <h1>Pokemones cargados</h1>
            )}
          </div>
          <div class="col-sm">una de dos</div>
        </div>
      </div>
    </div>
  );
};

export default Home;
